package com.venada.efinance.controller;import com.venada.efinance.pojo.DoubleEleven;import com.venada.efinance.service.DoubleElevenService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;import java.util.HashMap;import java.util.List;import java.util.Map;@Controllerpublic class DoubleElevenController {		@Autowired	private DoubleElevenService doubleElevenService;		@RequestMapping(value="/doubleEleven.html")	public String doubleEleven(Model model){		Map<String,Integer> limit = new HashMap<String,Integer>() ;		limit.put("startIndex", 0);		limit.put("endIndex", 20);		List<DoubleEleven> res =  doubleElevenService.findObjects("selectTop", limit);		if(res.size() > 0){			model.addAttribute("time",res.get(0).getAddtime());		}		model.addAttribute("res", res);		return ".doubleEleven";	}}