package com.venada.efinance.pojo;import com.venada.efinance.common.pojo.BaseEntity;import java.math.BigDecimal;import java.util.Date;/** * 充值流水号实体类 *  * @author yma *  */@SuppressWarnings("serial")public class RechargeRecord extends BaseEntity {	/**	 * 流水号	 */	private String serialNumber;	/**	 * 充值时间	 */	private Date dateTime;	/**	 * 充值状态，	 */	private String status;	/**	 * IP地址	 */	private String ipAddress;	/**	 * 充值金额	 */	private BigDecimal amount;	private BigDecimal balance;	private String description;	private String userId;		private User user;	public String getSerialNumber() {		return serialNumber;	}	public void setSerialNumber(String serialNumber) {		this.serialNumber = serialNumber;	}	public Date getDateTime() {		return dateTime;	}	public void setDateTime(Date dateTime) {		this.dateTime = dateTime;	}	public String getStatus() {		return status;	}	public void setStatus(String status) {		this.status = status;	}	public String getIpAddress() {		return ipAddress;	}	public void setIpAddress(String ipAddress) {		this.ipAddress = ipAddress;	}	public BigDecimal getAmount() {		if(amount != null){			amount = amount.setScale(2,BigDecimal.ROUND_DOWN);		}		return amount;	}	public void setAmount(BigDecimal amount) {		this.amount = amount;	}	public BigDecimal getBalance() {		if(balance != null){			balance = balance.setScale(2,BigDecimal.ROUND_DOWN);		}		return balance;	}	public void setBalance(BigDecimal balance) {		this.balance = balance;	}	public String getDescription() {		return description;	}	public void setDescription(String description) {		this.description = description;	}	public String getUserId() {		return userId;	}	public void setUserId(String userId) {		this.userId = userId;	}	public User getUser() {		return user;	}	public void setUser(User user) {		this.user = user;	}	}